    public int[] stringToInts(String cadena) {
        char[] toCharArray = cadena.toCharArray();
        int salida[] = new int[toCharArray.length];
        for (int i = 0; i < toCharArray.length; i++) {
            salida[i] = toCharArray[i];
        }
        return salida;
    }
    
