private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    //se establecen atributos que puedan almacenar los valores a mostrar
    String byteregister;
    String totalstring = "";
    String text = jTextArea1.getText();
//    int byite = Integer.parseInt(jTextField1.getText());
    String byite = jTextField1.getText();    
    int[] stringtext = stringToInts(byite);
    for(int i=0;i<stringtext.length;i++){
        byteregister = Integer.toHexString(stringtext[i]);
        totalstring = totalstring+"0x"+byteregister+" ";
    }
    jTextArea1.setText(text+"byte enviado: "+totalstring+"\n");
    jTextField1.setText("");
}//GEN-LAST:event_jButton1ActionPerformed

